double integrate(function<double(double)> &f,double l,double r) {
    double ans=0;
    for(int i=0;i<64;++i) {
        double x=roots64[i]*(r-l)/2.0+(r+l)/2.0;
        ans+=f(x)*we64[i];
    }
    return ans*(r-l)/2.0;
}
double integrate(function<double(double)> &f,double l,double r,int N) {
    double ans=0;
    for(int i=0;i<N;++i) {
        double l1=(l*(N-i)+r*i)/N;
        double r1=(l*(N-i-1)+r*(i+1))/N;
        ans+=integrate(f,l1,r1);
    }
    return ans;
}
double integrate(function<double(double)> &f,double l,double r,vector<double> cutpoints) { ///cutpoints in (0,1) , sorted
    double ans=0;
    cutpoints.insert(cutpoints.begin(),0);cutpoints.app(1);
    for(int i=0;i<cutpoints.size()-1;++i) {
        ans+=integrate(f,l*(1-cutpoints[i])+r*cutpoints[i],l*(1-cutpoints[i+1])+r*cutpoints[i+1]);
    }
    return ans;
}
